import React from 'react';
import fetch from 'isomorphic-unfetch';
import './styles.css';

function Statistics({ cases }) {
  const todayCases = cases[0];

  return (
    <section className="section section-dark">
      <h1 className="section-title">Estat√≠sticas do Brasil </h1>
      <div className="section-content ">{/* <Statistics /> */}</div>
      <div className="covid-infos">
        <div className="covid-info-item">
          <p>{todayCases.Confirmed}</p>
          <span> Confirmados</span>
        </div>

        <div className="covid-info-item">
          <p>{todayCases.Deaths}</p>
          <span> Mortes</span>
        </div>

        <div className="covid-info-item">
          <p>{todayCases.Recovered}</p>
          <span> Recuperados</span>
        </div>
      </div>
    </section>
  );
}

Statistics.getInitialProps = async function () {
  const response = await fetch(
    'https://api.covid19api.com/live/country/brazil'
  );
  const data = await response.json();
  return { cases: data };
};

export default Statistics;
