import React, { useEffect } from 'react';
import Router from 'next/router';
import auth0 from '../lib/auth0';

export default function App({ loggedIn = false, user }) {
  useEffect(() => {
    if (loggedIn) return;
    Router.push('/');
  }, [loggedIn]);

  return <h1>Bem vindo {user.name}</h1>;
}

// Processa o que tem de processar no servidor e manda para a pagina
// Funcina como uma API porém com tela
export async function getServerSideProps({ req, res }) {
  // pega o cookie da sessão do usuario
  const session = await auth0.getSession(req);
  if (session) {
    return {
      props: {
        loggedIn: true,
        user: session.user,
      },
    };
  }
  return {
    props: {
      loggedIn: false,
    },
  };
}
