/*
Espirros
Nariz entupido
Coriza
Dor de garganta
Tosse
Gotejamento pós-nasal
Olhos marejados
Possivelmente febre (mas a maioria das pessoas com resfriados não tem febre)
Os sintomas de um resfriado geralmente atingem o pico de dois a três dias após a contração, diz o CDC.

E, de acordo com o CDC, estes são os sintomas do coronavírus:

Febre
Tosse
Falta de ar
*/

const influenzaSymptoms = [
  'Coriza',
  'Dor de garganta',
  'Dor de cabeça',
  'Cansaço',
  'Espirro',
  'Tosse',
  'Febre',
];

const covidSymptoms = [
  'Dificuldade de respirar',
  'Fadiga',
  'Fadiga',
  'Febre',
  'Tosse',
  'Diarreia',
];

export default function diagnostic(symptoms) {
  let contInfluenza = 0;
  let contCovid = 0;

  symptoms.forEach((symptom) => {
    if (covidSymptoms.includes(symptom)) {
      contCovid += 1;
    }

    if (influenzaSymptoms.includes(symptom)) {
      contInfluenza += 1;
    }

    if (
      influenzaSymptoms.includes(symptom) &&
      covidSymptoms.includes(symptom)
    ) {
      contInfluenza += 1;
      contCovid += 1;
    }
  });

  if (contInfluenza > contCovid) {
    return 'suspect';
  }

  if (contCovid > contInfluenza) {
    return 'suspect';
  }

  return 'healthy';
}
