import React, { useEffect } from 'react';
import Router from 'next/router';
import AppMenu from '../components/AppMenu';
import Loading from '../components/Loading';

import auth0 from '../lib/auth0';
import getCurrentDate from '../util/getCurrentDate';
import { db } from '../lib/firebase';

import '../styles/app.css';

export default function App({ isAuth, hasRegisteredStatusToday, user }) {
  useEffect(() => {
    if (!isAuth) Router.push('/');
    if (!hasRegisteredStatusToday) Router.push('/status');
  }, [isAuth]);

  if (isAuth) {
    return (
      <div className="app-container">
        <AppMenu user={user} />
      </div>
    );
  }
  return (
    <div className="center-content fullscreen">
      <Loading />
    </div>
  );
}

// Processa o que tem de processar no servidor e manda para a pagina
// Funcina como uma API porém com tela
export async function getServerSideProps({ req, res }) {
  const session = await auth0.getSession(req);

  if (!session) {
    return {
      props: {
        isAuth: false,
      },
    };
  }

  const currentDate = getCurrentDate();

  const todaysCheckin = await db
    .collection('markers')
    .doc(currentDate)
    .collection('checks')
    .doc(session.user.sub)
    .get();

  const todayRegistered = todaysCheckin.data();

  if (!todayRegistered) {
    return {
      props: {
        isAuth: true,
        hasRegisteredStatusToday: false,
        user: session.user,
      },
    };
  }

  const { coordinates } = todayRegistered;

  const checkins = await db
    .collection('markers')
    .doc(currentDate)
    .collection('checks')
    .near({
      center: coordinates,
      radius: 1000,
    })
    .get();

  // checkins.docs.forEach((doc) => {
  //   console.log(doc);
  // });

  return {
    props: {
      isAuth: true,
      hasRegisteredStatusToday,
      user: session.user,
    },
  };
}
