import { db } from '../../lib/firebase';
import auth0 from '../../lib/auth0';

import getCurrentDate from '../../util/getCurrentDate';

export default async function (req, res) {
  const { filter, latitude, longitude } = req.query;
  const { userId } = req.headers;

  /* Caso o usuário já tenha cadastrado seu status diário, será verificado
   * todos os usúarios que estao proximos dele
   */

  // .where('status', '==', filter)

  const nearbyUsers = await db
    .collection('history')
    .doc('2020-04-20')
    .collection('all')
    .near({
      center: { latitude, longitude },
      radius: 10000,
    })
    .get();

  const nearbyUsersList = [];

  nearbyUsers.docs.forEach((doc) => {
    nearbyUsersList.push({
      id: doc.id,
      status: doc.data().status,
      coordinates: {
        longitude: doc.data().coordinates.longitude,
        latitude: doc.data().coordinates.latitude,
      },
    });
  });

  return res.status(200).json({
    props: {
      isAuth: true,
      hasRegisteredDailyStatus: true,
      // user: session.user,
      nearbyUsers: nearbyUsersList,
    },
  });
}
