import { db, admin } from '../../../lib/firebase';
import auth0 from '../../../lib/auth0';

export default async function store(req, res) {
  const { status } = req.body;
  const { latitude, longitude } = req.body.coords;

  const session = auth0.getSession(req);

  if (!session) return res.status(401).json({ error: 'Unauthenticated user' });
  const currentDate = new Date().toISOString().split('T')[0];
  const { sub: userId } = (await session).user;

  await db
    .collection('markers')
    .doc(currentDate)
    .collection('checks')
    .doc(userId)
    .set({
      status,
      userId,
      coords: new admin.firestore.GeoPoint(latitude, longitude),
    });

  return res.send({ osk: true });
}
