import React, { useEffect } from 'react';
import Router from 'next/router';
import AppMenu from '../components/AppMenu';
import Loading from '../components/Loading';

import auth0 from '../lib/auth0';
import { db } from '../lib/firebase';

import '../styles/app.css';

export default function App({ isAuth, hasRegisteredStatusToday, user }) {
  useEffect(() => {
    if (!isAuth) Router.push('/');
    if (!hasRegisteredStatusToday) Router.push('create-status');
  }, [isAuth]);

  if (isAuth) {
    return (
      <div className="app-container">
        <AppMenu user={user} />
      </div>
    );
  }
  return (
    <div className="loading-container">
      <Loading />
    </div>
  );
}

// Processa o que tem de processar no servidor e manda para a pagina
// Funcina como uma API porém com tela
export async function getServerSideProps({ req, res }) {
  // pega o cookie da sessão do usuario
  const session = await auth0.getSession(req);
  if (session) {
    // console.log(session.user);

    const todaysCheckin = await db
      .collection('markers')
      .doc('2020-04-15')
      .collection('checks')
      .doc(session.user.sub)
      .get();

    const todayRegistered = todaysCheckin.data();
    const hasRegisteredStatusToday = !!todayRegistered;

    return {
      props: {
        isAuth: true,
        hasRegisteredStatusToday,
        user: session.user,
      },
    };
  }

  return {
    props: {
      isAuth: false,
    },
  };
}
