import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Router from 'next/router';
import AppMenu from '../components/AppMenu';
import Loading from '../components/Loading';
import Map from '../components/Map';

import auth0 from '../lib/auth0';
import getCurrentDate from '../util/getCurrentDate';
import { db } from '../lib/firebase';

import '../styles/app.css';

export default function App({
  isAuth,
  hasRegisteredDailyStatus,
  user,
  nearbyUsers = [],
}) {
  const [coords, setCoords] = useState({ latitude: null, longitude: null });

  function getUserLocation() {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((position) => {
        const { latitude, longitude } = position.coords;
        setCoords({ latitude, longitude });
      });
    } else {
      alert('Seu navegador não possui suporte a geolocalização');
      Router.push('/');
    }
  }

  useEffect(async () => {
    if (!isAuth) Router.push('/');
    if (!hasRegisteredDailyStatus) Router.push('/status');

    getUserLocation();

    const response = await axios.get('/api/nearby-users', {
      query: {
        latitude: coords.latitude,
        longitude: coords.longitude,
      },
    });
    // await axios.post('/api/user/')
  }, []);

  if (isAuth && coords.latitude && coords.longitude) {
    return (
      <>
        <AppMenu user={user} />
        <div className="app-container">
          <Map
            nearbyUsers={nearbyUsers}
            user={user}
            defaultCenter={{
              lat: coords.latitude,
              lng: coords.longitude,
            }}
            zoom={13}
          />
        </div>
      </>
    );
  }

  return (
    <div className="center-content fullscreen">
      <Loading />
    </div>
  );
}
