import React, { useState, useEffect } from 'react';
import Router from 'next/router';
import axios from 'axios';
import auth0 from '../lib/auth0';

import AppMenu from '../components/AppMenu';
import Loading from '../components/Loading';
import Footer from '../components/Footer';
import Symptom from '../components/Symptom';

import symptoms from '../json/symptoms.json';

import '../styles/status.css';

export default function status({ user, isAuth }) {
  const [coords, setCoords] = useState({ latitude: null, longitude: null });
  const [status, setStatus] = useState('bem');
  const [selectedSymptoms, setSelectedSymptoms] = useState([]);

  function getUserLocation() {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((position) => {
        const { latitude, longitude } = position.coords;
        setCoords({ latitude, longitude });
      });
    } else {
      alert('Seu navegador não possui suporte a geolocalização');
      Router.push('/');
    }
  }

  function selectSymptom(key) {
    setSelectedSymptoms([...selectedSymptoms, key]);
  }

  async function handleSave() {
    const response = await axios.post('/api/status/store', {
      status,
      coords,
    });
  }

  useEffect(() => {
    if (!isAuth) Router.push('/');

    getUserLocation();
  }, [isAuth]);

  if (isAuth) {
    return (
      <>
        <AppMenu user={user} />
        <div className="status-container">
          <div className="content">
            <div className="nurse-container">
              <img src="assets/icons/nurse.svg" alt="Nurse icon" />
              <p>
                Como você está se sentindo hoje, Thiago Henrique Domingues?
                Selecione a baixo os sintomas que você esta sentindo. Caso não
                esteja apresentando nenhum, clique em continuar.{' '}
              </p>
            </div>

            <div className="symptoms-container">
              {symptoms.map((symptom) => {
                return (
                  <Symptom
                    key={symptom.key}
                    selected={selectedSymptoms.find(symptom.key)}
                    symptom={symptom}
                  />
                );
              })}
            </div>
          </div>
        </div>
        <Footer />
      </>
    );
  }

  return (
    <div className="center-content fullscreen">
      <Loading />
    </div>
  );
}

export async function getServerSideProps({ req, res }) {
  // pega o cookie da sessão do usuario
  const session = await auth0.getSession(req);
  if (session) {
    req.user = session.user;

    return {
      props: {
        isAuth: true,
        user: session.user,
      },
    };
  }

  return {
    props: {
      isAuth: false,
    },
  };
}
