import React, { useEffect } from 'react';
import Router from 'next/router';

import format from 'date-fns/format';

import pt from 'date-fns/locale/pt-BR';

import AppMenu from '../components/AppMenu';
import Loading from '../components/Loading';
import auth0 from '../lib/auth0';

import { db } from '../lib/firebase';

import '../styles/profile.css';

export default function profile({ user, history, isAuth }) {
  useEffect(() => {
    if (!isAuth) Router.push('/');
  }, [isAuth]);

  if (isAuth) {
    return (
      <>
        <AppMenu user={user} />

        <div className="profile-container">
          <div className="content">
            <div className="header">
              <img src={user.picture} />
              <h1>{user.name}</h1>
              <p>Arcos/MG</p>
            </div>

            <div className="symptoms-history">
              <h1> Hist√≥rico </h1>

              {history.map((day) => {
                const dateFormated = format(new Date(day.date), 'dd-LLLL', {
                  locale: pt,
                });

                return (
                  <div className="symptom-card">
                    <div className="day-cirlce">
                      <b>04</b>
                      <span>{dateFormated.split()[0]} </span>
                    </div>

                    <div className="symptoms">
                      <b> Suspeito de Covid-19 </b>
                      <p> Tosse, dor no corpo, febre, dor nos olhos </p>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </>
    );
  }

  return (
    <div className="center-content fullscreen">
      <Loading />
    </div>
  );
}

export async function getServerSideProps({ req, res }) {
  const session = await auth0.getSession(req);

  if (session) {
    req.user = session.user;
    const userId = session.user.sub;

    const symptomsHistory = await db
      .collection('users')
      .doc(userId)
      .collection('history')
      .get();

    const historyList = [];

    symptomsHistory.docs.map((item) => {
      historyList.push({
        date: item.id,
        status: item.data().status,
        symptoms: item.data().symptoms,
      });
    });

    console.log(symptomsHistory.docs);

    return {
      props: {
        isAuth: true,
        user: session.user,
        history: historyList,
      },
    };
  }

  return {
    props: {
      isAuth: false,
    },
  };
}
