import React from 'react';
import GoogleMapReact from 'google-map-react';
import { FaCompass, FaSearch } from 'react-icons/fa';

import UserMarker from '../UserMarker';
import ContaminatedMarker from '../ContaminatedMarker';
import VirusSvg from '../../public/assets/icons/virus.svg';

import './styles.css';

const Map = ({ user, defaultCenter, nearbyUsers, zoom = 15 }) => {
  return (
    <div className="map-container">
      <GoogleMapReact
        bootstrapURLKeys={{ key: process.env.GOOGLE_MAPS_API_KEY }}
        defaultCenter={defaultCenter}
        defaultZoom={zoom}
        options={{ fullscreenControl: false, zoomControl: false }}
      >
        <UserMarker
          user={user}
          lat={defaultCenter.lat}
          lng={defaultCenter.lng}
        />

        {nearbyUsers.map((people) => (
          <ContaminatedMarker
            key={people.id}
            lat={people.coordinates.latitude}
            lng={people.coordinates.longitude}
            data={people}
          />
        ))}
      </GoogleMapReact>

      <div className="filter-bar">
        <div className="options-container">
          <div className="input-container">
            <div className="icon">
              <FaCompass color="#fff" size={24} />
            </div>
            <input type="number" placeholder="Raio de busca" />
          </div>

          <div className="select-container">
            <div className="icon">
              <VirusSvg fill="#fff" />
            </div>
            <select>
              <option>Todos as pessoas</option>
              <option>Covid</option>
            </select>
          </div>

          <button
            type="button"
            onClick={() => alert('o')}
            className="search-button"
          >
            ok
            <FaSearch color="#fff" size={16} />
          </button>
        </div>
      </div>
    </div>
  );
};

export default Map;
