import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Router from 'next/router';
import AppMenu from '../components/AppMenu';
import Loading from '../components/Loading';
import Map from '../components/Map';

import auth0 from '../lib/auth0';
import getCurrentDate from '../util/getCurrentDate';
import { db } from '../lib/firebase';

import '../styles/app.css';

export default function App({ isAuth, hasRegisteredDailyStatus, user }) {
  const [coords, setCoords] = useState({ latitude: null, longitude: null });
  const [nearbyUsers, setNearbyUsers] = useState([]);

  function getUserLocation() {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((position) => {
        const { latitude, longitude } = position.coords;
        setCoords({ latitude, longitude });
      });
    } else {
      alert('Seu navegador não possui suporte a geolocalização');
      Router.push('/');
    }
  }

  useEffect(() => {
    if (!isAuth) Router.push('/');
    if (!hasRegisteredDailyStatus) Router.push('/status');

    getUserLocation();

    async function getNearbUsers() {
      const response = await axios.get('/api/nearby-users', {
        query: {
          latitude: coords.latitude,
          longitude: coords.longitude,
          filter: 'all',
        },
        headers: {
          userId: user.sub,
        },
      });

      const { data } = response;
      setNearbyUsers([1]);
      console.log(data);
    }

    getNearbUsers();
  }, []);

  if (isAuth && coords.latitude && coords.longitude) {
    return (
      <>
        <AppMenu user={user} />
        <div className="app-container">
          {/* <Map
            nearbyUsers={nearbyUsers}
            user={user}
            defaultCenter={{
              lat: coords.latitude,
              lng: coords.longitude,
            }}
            zoom={13}
          /> */}
        </div>
      </>
    );
  }

  return (
    <div className="center-content fullscreen">
      <Loading />
    </div>
  );
}

export async function getServerSideProps({ req, res }) {
  const session = await auth0.getSession(req);

  // Verifica se o usuário está logado
  if (!session) {
    return {
      props: {
        isAuth: false,
      },
    };
  }

  /*
   * Verifica se o usuário já cadastrou seu status diario
   */
  const currentDate = getCurrentDate();

  const userDailyStatus = await db
    .collection('users')
    .doc(session.user.sub)
    .collection('history')
    .doc(currentDate)
    .get();

  const todayRegistered = userDailyStatus.data();
  if (!todayRegistered) {
    return {
      props: {
        isAuth: true,
        hasRegisteredDailyStatus: false,
        user: session.user,
      },
    };
  }

  return {
    props: {
      isAuth: true,
      hasRegisteredDailyStatus: true,
      user: session.user,
    },
  };
}
